class JsonPlaceholder extends Component {

    state={
        items: [],
        isLoaded: false
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res=> res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    items: json,
                })
            });
    }

    render() {
        
