import React, { Component } from "react";

class JsonPlaceholder extends Component {
  state = {
    items: [],
    isLoaded: false,
    albumId: 1,
    totalAlbum : null
  };

  componentDidMount() {
    let {albumId} = this.state;
    const url = `https://jsonplaceholder.typicode.com/albums/1/photos?albumId=${albumId}`
    fetch(url)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  render() {
    let { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div className="loader"></div>;
    } else {
      return (
        <div style={{display:"flex", flexWrap:"wrap", width:"1170px", margin:"auto"}}> 
          {items.map(item => (
            <div style={{borderBottom: "solid 1px #ccc", padding: "20px", width:"150px"}}>
             
              <p><img src={item.thumbnailUrl}/></p>
              <p>{item.id} {item.title}</p>
            </div>
            
          ))}
          <a onClick={this.loadMore}>Load More</a>
        </div>
      );
    }
  }
}

export default JsonPlaceholder;
