import React, { Component } from "react";
import './Comment.css'
class CommentAPI extends Component {
  state = {
    items: [],
    isLoaded: false
  };

componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/comments')
    .then(res => res.json())
    .then(xyz => {
        this.setState({
            isLoaded: true,
            items: xyz
        })
    })
}
  render() {
      let {items, isLoaded} = this.state;
    return <h1 className="loader"></h1>;
  }
}
export default CommentAPI;
