import React, { Component } from "react";

class CommentAPI extends Component {
  state = {
    items: [],
    isLoaded: false
  };

componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/comments')
    .then(res => json())
    .then(json => {
        this.setState({
            isLoaded: true,
            items: json
        })
    })
}
  render() {
    return <h1>Comments</h1>;
  }
}
export default CommentAPI;
